mapper.select:
SELECT il.*, sh.name as shopName FROM `interface_log` il LEFT JOIN shop sh ON il.shop_id=sh.id where il.`status`=1
and sh.id =#{interfaceLogQuery.shopId}
and il.create_time >=#{interfaceLogQuery.begin,jdbcType=TIMESTAMP}
and il.create_time <= #{interfaceLogQuery.end,jdbcType=TIMESTAMP}
order by il.id desc

SELECT il.*, sh.name as shopName FROM `interface_log` il LEFT JOIN shop sh ON il.shop_id=sh.id where il.`status`=1 and il.shop_id=#{shopId}
and il.create_time >=#{interfaceLogQuery.begin,jdbcType=TIMESTAMP}
and il.create_time <= #{interfaceLogQuery.end,jdbcType=TIMESTAMP}
order by il.id desc
